mapper.select:
SELECT schedule_id , task_id , start_time , end_time , dt ,
       success_num , failed_num , request_num , data_num , surplus_num
FROM cloud_crawler_schedule WHERE task_id = #{taskId}

mapper.update:
UPDATE
  cloud_crawler_schedule
SET
  end_time = #{endTime}
WHERE
  schedule_id = #{scheduleId};
